package merkleutils_test

import (
	"testing"

	"github.com/ethereum/go-ethereum/common/hexutil"
	"github.com/ethereum/go-ethereum/crypto"
	"github.com/stretchr/testify/require"

	"github.com/smartcontractkit/chainlink/v2/core/services/ocr2/plugins/liquiditymanager/bridge/opstack/merkleutils"
)

// Test suite taken from: https://github.com/ethereum-optimism/optimism/blob/f7920585f1ef280f94546d708fbb0a61bb77ed3e/packages/sdk/test/utils/merkle-utils.spec.ts
func TestMaybeAddProofNode(t *testing.T) {
	type testCase struct {
		name     string
		key      [32]byte
		proof    [][]byte
		expected [][]byte
	}
	tests := []testCase{
		{
			// Test taken from opBNB testnet withdrawal.
			name: "should add a proof node when final node is inside of a branch",
			key:  mustHash("0x5cd60ecef836e75dd12539abe8ad5f4ba0dc4fcaae2c45024c69bd9e20746eba"),
			proof: [][]byte{
				hexutil.MustDecode(""),
				hexutil.MustDecode(""),
				hexutil.MustDecode(""),
				hexutil.MustDecode(""),
				hexutil.MustDecode(""),
				hexutil.MustDecode("0xf89180808080808080a0326fe6fc4a847e4db1144153b15d0d3811f0317239f2885cc2a383825ccb1d8480a04455f6e0d1f8a2022ea33d5e6a537e384483dfccd19d07d21047e9b264b56e3c808080a06174ffa3ff6accab10abb5d61000db050324c608b10c0344a3eec6fa0107a079a0d494a04c23ba556fc3bdbe234562fe6825d55f3aaaa81e43415b8070a733d0bd8080"),
				hexutil.MustDecode("0xe482000fa03cb626e2849a157c57ca2e62c3dd139cf803efe281f5b6331b3ba92280dd8c42"),
				hexutil.MustDecode("0xf84d8080808080de9c332c35a4d03ec6ab9b3ffd06c69652ce8e02ff95537f98b7a0feb29c01808080de9c36620d27102d4799d259fe08f690c0a21b80d0a1a903db682417a23f0180808080808080"),
			},
			expected: [][]byte{
				hexutil.MustDecode(""),
				hexutil.MustDecode(""),
				hexutil.MustDecode(""),
				hexutil.MustDecode(""),
				hexutil.MustDecode(""),
				hexutil.MustDecode("0xf89180808080808080a0326fe6fc4a847e4db1144153b15d0d3811f0317239f2885cc2a383825ccb1d8480a04455f6e0d1f8a2022ea33d5e6a537e384483dfccd19d07d21047e9b264b56e3c808080a06174ffa3ff6accab10abb5d61000db050324c608b10c0344a3eec6fa0107a079a0d494a04c23ba556fc3bdbe234562fe6825d55f3aaaa81e43415b8070a733d0bd8080"),
				hexutil.MustDecode("0xe482000fa03cb626e2849a157c57ca2e62c3dd139cf803efe281f5b6331b3ba92280dd8c42"),
				hexutil.MustDecode("0xf84d8080808080de9c332c35a4d03ec6ab9b3ffd06c69652ce8e02ff95537f98b7a0feb29c01808080de9c36620d27102d4799d259fe08f690c0a21b80d0a1a903db682417a23f0180808080808080"),
				hexutil.MustDecode("0xde9c36620d27102d4799d259fe08f690c0a21b80d0a1a903db682417a23f01"), // new proof node
			},
		},
		{
			name: "should not add a proof node when final node is a leaf node",
			key:  mustHash("0x55b08006167f308d31af5ea9f302384725df906124e39e0eb2c6d040498e0fae"),
			proof: [][]byte{
				hexutil.MustDecode(""),
				hexutil.MustDecode(""),
				hexutil.MustDecode(""),
				hexutil.MustDecode(""),
				hexutil.MustDecode(""),
				hexutil.MustDecode("0xf851808080a045d583df49281f7f8c545ab91f4d35ba48873cc4da6deb2c0703135959fc8ec080808080a0ca2836f17b4c1af21e2531f7036a069ba8ef535650495820731ca669590dfddb8080808080808080"),
				hexutil.MustDecode("0xe09e202ec4eafbdfe1b7f218e5f087854c923c7f41b85d3ada15c32bef29588a01"),
			},
			expected: [][]byte{
				hexutil.MustDecode(""),
				hexutil.MustDecode(""),
				hexutil.MustDecode(""),
				hexutil.MustDecode(""),
				hexutil.MustDecode(""),
				hexutil.MustDecode("0xf851808080a045d583df49281f7f8c545ab91f4d35ba48873cc4da6deb2c0703135959fc8ec080808080a0ca2836f17b4c1af21e2531f7036a069ba8ef535650495820731ca669590dfddb8080808080808080"),
				hexutil.MustDecode("0xe09e202ec4eafbdfe1b7f218e5f087854c923c7f41b85d3ada15c32bef29588a01"),
			},
		},
		{
			name: "should not add a proof node when the value itself is inside a branch node",
			key:  padLeft("0x6b657933"),
			proof: [][]byte{
				hexutil.MustDecode("0xe68416b65793a0f3f387240403976788281c0a6ee5b3fc08360d276039d635bb824ea7e6fed779"),
				hexutil.MustDecode("0xf87180a034d14ccc7685aa2beb64f78b11ee2a335eae82047ef97c79b7dda7f0732b9f4ca05fb052b64e23d177131d9f32e9c5b942209eb7229e9a07c99a5d93245f53af18a09a137197a43a880648d5887cce656a5e6bbbe5e44ecb4f264395ccaddbe1acca80808080808080808080808080"),
				hexutil.MustDecode("0xf839808080808080c9823363856176616c338080808080808080809f31323334353637383930313233343536373839303132333435363738393031"),
			},
			expected: [][]byte{
				hexutil.MustDecode("0xe68416b65793a0f3f387240403976788281c0a6ee5b3fc08360d276039d635bb824ea7e6fed779"),
				hexutil.MustDecode("0xf87180a034d14ccc7685aa2beb64f78b11ee2a335eae82047ef97c79b7dda7f0732b9f4ca05fb052b64e23d177131d9f32e9c5b942209eb7229e9a07c99a5d93245f53af18a09a137197a43a880648d5887cce656a5e6bbbe5e44ecb4f264395ccaddbe1acca80808080808080808080808080"),
				hexutil.MustDecode("0xf839808080808080c9823363856176616c338080808080808080809f31323334353637383930313233343536373839303132333435363738393031"),
			},
		},
	}
	for _, test := range tests {
		t.Run(test.name, func(t *testing.T) {
			modified, err := merkleutils.MaybeAddProofNode(test.key, test.proof)
			require.NoError(t, err)
			require.Equal(t, test.expected, modified)
		})
	}
}

func mustHash(s string) [32]byte {
	decoded := hexutil.MustDecode(s)
	return crypto.Keccak256Hash(decoded)
}

func padLeft(s string) [32]byte {
	decoded := hexutil.MustDecode(s)
	for len(decoded) < 32 {
		decoded = append([]byte{0}, decoded...)
	}
	var result [32]byte
	copy(result[:], decoded)
	return result
}
